-- Update existing work requests with assigned github repo info
UPDATE	WorkRequests
SET		AssignedTrialRepositoryId = tgi.GitHubRepositoryId
FROM	TrialRepositoryInfos AS tgi
WHERE	tgi.TrialId = WorkRequests.TrialId
AND		WorkRequests.GitHubIssueNumber IS NOT NULL;

